use tokio::net::TcpListener;
use tokio::io::{AsyncReadExt, AsyncWriteExt};
use std::error::Error;

#[tokio::main]
async fn main() -> Result<(), Box<dyn Error>> {
    let listener = TcpListener::bind("127.0.0.1:3000").await?;
    println!("üöÄ Server running on http://127.0.0.1:3000");

    loop {
        let (mut socket, addr) = listener.accept().await?;
        println!("üì° New connection from: {}", addr);

        tokio::spawn(async move {
            let mut buffer = [0; 1024];

            match socket.read(&mut buffer).await {
                Ok(n) if n == 0 => return,
                Ok(n) => {
                    let request = String::from_utf8_lossy(&buffer[0..n]);
                    println!("üìù Request: {}", request);

                    let response = "HTTP/1.1 200 OK\r\n\r\nHello from Rust server!";
                    if let Err(e) = socket.write_all(response.as_bytes()).await {
                        println!("‚ùå Failed to write response: {}", e);
                    }
                }
                Err(e) => {
                    println!("‚ùå Failed to read from socket: {}", e);
                }
            }
        });
    }
}

class ApiClient {
  constructor(baseUrl, apiKey) {
    this.baseUrl = baseUrl;
    this.apiKey = apiKey;
    this.headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${apiKey}`
    };
  }

  async get(endpoint) {
    try {
      const response = await fetch(`${this.baseUrl}${endpoint}`, {
        method: 'GET',
        headers: this.headers
      });
      return await this.handleResponse(response);
    } catch (error) {
      throw new Error(`GET request failed: ${error.message}`);
    }
  }

  async post(endpoint, data) {
    try {
      const response = await fetch(`${this.baseUrl}${endpoint}`, {
        method: 'POST',
        headers: this.headers,
        body: JSON.stringify(data)
      });
      return await this.handleResponse(response);
    } catch (error) {
      throw new Error(`POST request failed: ${error.message}`);
    }
  }

  async handleResponse(response) {
    if (!response.ok) {
      throw new Error(`HTTP ${response.status}: ${response.statusText}`);
    }
    return await response.json();
  }
}

// Example usage
const client = new ApiClient('https://api.example.com', 'your-api-key');

// Get user data
client.get('/users/123')
  .then(user => console.log('User:', user))
  .catch(error => console.error('Error:', error));

// Create new user
client.post('/users', {
  name: 'John Doe',
  email: 'john@example.com'
})
  .then(result => console.log('Created:', result))
  .catch(error => console.error('Error:', error));
